module.exports = testwrapper;

var test = require("tape"),
    sinon = require("sinon");

function testwrapper(beforeEach) {
    
    return function(testDescription, testBody) {
        
        test(
            testDescription,
            function(t) {
                
                if (beforeEach) {
                    beforeEach();
                }
                
                sinon.assert.pass = function() {
                    t.pass();
                };
                
                testBody(t);
            }
        );

    }
    
}